            $this->load->view('layouts/main',$data);
        }
    }  

    /*
     * Editing a lecturerroom
     */
    function edit($ID)
    {   
        // check if the lecturerroom exists before trying to edit it
        $data['lecturerroom'] = $this->Lecturerroom_model->get_lecturerroom($ID);
        
        if(isset($data['lecturerroom']['ID']))
        {
            if(isset($_POST) && count($_POST) > 0)     
            {   
                $params = array(
					'departmentID' => $this->input->post('departmentID'),
					'name' => $this->input->post('name'),
					'dateCreated' => $this->input->post('dateCreated'),
                );

                $this->Lecturerroom_model->update_lecturerroom($ID,$params);            
                redirect('lecturerroom/index');
