     */
    function remove($ID)
    {
        $lecturerroom = $this->Lecturerroom_model->get_lecturerroom($ID);

        // check if the lecturerroom exists before trying to delete it
        if(isset($lecturerroom['ID']))
        {
            $this->Lecturerroom_model->delete_lecturerroom($ID);
